mapper.select:
select   
from sys_menu_category  t

	1=1
	and t.ID = #{id} 
	and t.NAME like '%' || #{name} || '%'
	and t.CODE = #{code} 
	and t.URL = #{url} 
	and t.ICON = #{icon} 
	and t.DESCRIPTION = #{description} 
	and t.SORT = #{sort} 
	and t.CREATE_BY = #{createBy} 
	and t.CREATE_DATE = #{createDate} 
	and t.UPDATE_BY = #{updateBy} 
	and t.UPDATE_DATE = #{updateDate} 
	and t.DEL_FLAG = #{delFlag} 
	 and ${dynamicSQL} 


ORDER BY t.SORT

